import React, { Component } from 'react';

class Form extends Component {

  constructor(props){
    super(props);
    this.state = {
      txtName : '',
      txtTel : '',
      sltGender : 0,

    }
  }

  onHandleChange(event){

  }

  render() {
    return (
      <div className="panel panel-default">
        <div className="panel-body">
          <div className="form-group">
            <label>Customer's info</label>
            <p>Name</p>
            <input type="text" className="form-control" name="txtName" value="Input name here" onChange={this.onHandleChange}/>
            <br/>
            <p>Phone number</p>
            <input type="text" name="txtTel" className="form-control" value="Input phone number here"/>
            <br/>
            <p>Gender</p>
            <select name="sltGender" id="input" className="form-control" required="required">
              <option value="f">(Gender)</option>
              <option value="f">Female</option>
              <option value="m">Male</option>
            </select>
            <br/>
            <p>Address</p>
            <textarea name="txtAddr" id="inputTxtAddr" className="form-control" rows="3" value="Input address here"/>
            <div class="radio">
              <label>
                <input type="radio" name="payment" className="rbPrepay" checked="checked" />
                Prepay
              </label>
              &nbsp;
              <label>
                <input type="radio" name="payment" className="rbPostpay" />
                Postpay
              </label>
            </div>
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </div>
      </div>
    );
  }
}

export default Form;
