import React, { Component } from 'react';
import Product from './components/Product';
import Form from './components/Form';

class App extends Component {

  constructor(props){
    super(props);
    this.onAddProduct = this.onAddProduct.bind(this);
  }

  onAddProduct(){
    console.log(this.refs)
  }

  render() {
    var products = [
      {
        id : 1,
        name : 'Iphone X',
        price : 18000000,
        image : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzK2eeMKLsge654g_lZRwksVAzCWAJyjT_-IRoHVUNsUxIBe2G'
      },
      {
        id : 2,
        name : 'Iphone Xs',
        price : 22000000,
        image : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTbrK5IviMgLsE15DD4OWV5wDeESLdC-ojNeVRV4AugQOLq70E'
      },
      {
        id : 3,
        name : 'Iphone Xs Plus',
        price : 24000000,
        image : 'data:image/jpeg;base64,'
      }
    ];

    let elements = products.map((product, index) => {
      let result = '';
      if(index % 2 === 0){
        result = <Product 
                    key={product.id}
                    name={product.name}
                    price={product.price}
                    image={product.image}
                />
      }
      return result;
    });

    return (
      <div>
        <nav className="navbar navbar-inverse">
          <div className="container-fluid">
            <a className="navbar-brand" href="#a">Final</a>
            <ul className="nav navbar-nav">
              <li>
                <a href="#a">Product</a>
              </li>
              <li>
                <a href="#a">Form</a>
              </li>
            </ul>
          </div>
        </nav>
        <div>
          <div className="row">
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <div className="panel panel-danger">
                  <div className="panel-heading">Add product</div>
                  <div className="panel-body">
                    <div className="form-group">
                      <input type="text" className="form-control" ref="name"/>
                    </div>
                      <button type="submit" className="btn btn-primary" onClick={this.onAddProduct}>Submit</button>
                    </div>
                </div>
            </div>
            <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
              {elements}
            </div>
            <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">

            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
